t.script:
document.addEventListener('DOMContentLoaded', function () {
    const marketBreakdown = JSON.parse('');
    const locationData = JSON.parse('');
    const rankingData = JSON.parse('');

    // ðŸ”¹ Market Bids Breakdown (Total, Cash, Credit, Cheque, Bank Transfer)
    const marketChartEl = document.getElementById('energyTypeChart');
    if (marketChartEl) {
        const ctx = marketChartEl.getContext('2d');
        const labels = Object.keys(marketBreakdown);
        const values = Object.values(marketBreakdown);

        if (ctx) {
            new Chart(ctx, {
                type: 'bar',
                data: {
                    labels: labels,
                    datasets: [{
                        label: 'Number of Bids',
                        data: values,
                        backgroundColor: [
                            '#1170f0',
                            '#28a745',
                            '#ffc107',
                            '#6f42c1',
                            '#20c997'
                        ]
                    }]
                },
                options: {
                    responsive: true,
                    scales: {
                        y: {
                            beginAtZero: true,
                            title: {
                                display: true,
                                text: 'Number of Bids'
                            }
                        },
                        x: {
                            title: {
                                display: true,
                                text: 'Payment Method'
                            }
                        }
                    }
                }
            });
        }
    }

    // ðŸ”¸ Bids by Location
    const locationChartEl = document.getElementById('locationChart');
    if (locationChartEl) {
        const labels = locationData.map(l => l.location ? l.location[1] : 'Unknown');
        const values = locationData.map(l => l.volume_requested || 0);

        const ctx = locationChartEl.getContext('2d');
        if (ctx) {
            new Chart(ctx, {
                type: 'bar',
                data: {
                    labels: labels,
                    datasets: [{
                        label: 'Liters Ordered',
                        data: values,
                        backgroundColor: '#82b1ff'
                    }]
                },
                options: {
                    responsive: true,
                    scales: {
                        y: {
                            beginAtZero: true,
                            title: {
                                display: true,
                                text: 'Liters'
                            }
                        }
                    }
                }
            });
        }
    }

    // ðŸ”» Company Ranking (Top 5 horizontal bar)
    const companyChartEl = document.getElementById('companyRankingChart');
    if (companyChartEl) {
        rankingData.sort((a, b) => (b.volume_requested || 0) - (a.volume_requested || 0));
        const topCompanies = rankingData.slice(0, 5);
        const labels = topCompanies.map(c => c.label || 'Unknown');
        const values = topCompanies.map(c => c.volume_requested || 0);

        const ctx = companyChartEl.getContext('2d');
        if (ctx) {
            new Chart(ctx, {
                type: 'bar',
                data: {
                    labels: labels,
                    datasets: [{
                        label: 'Total Liters Ordered',
                        data: values,
                        backgroundColor: '#ffc107'
                    }]
                },
                options: {
                    indexAxis: 'y',
                    responsive: true,
                    plugins: {
                        legend: { display: false },
                        tooltip: {
                            callbacks: {
                                label: function (context) {
                                    return context.parsed.x + ' Liters';
                                }
                            }
                        }
                    },
                    scales: {
                        x: {
                            beginAtZero: true,
                            title: {
                                display: true,
                                text: 'Liters'
                            }
                        },
                        y: {
                            title: {
                                display: true,
                                text: 'Company'
                            }
                        }
                    }
                }
            });
        }
    }
});
